import React, { useState } from 'react';
import axios from 'axios';

function ExcelUploader() {
  const [layoutFile, setLayoutFile] = useState(null);
  const [flatFile, setFlatFile] = useState(null);
  const [parsedData, setParsedData] = useState([]);

  const handleLayoutChange = (e) => {
    setLayoutFile(e.target.files[0]);
  };

  const handleFlatFileChange = (e) => {
    setFlatFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!layoutFile || !flatFile) {
      alert('Please upload both layout and flat files.');
      return;
    }

    const formData = new FormData();
    formData.append('layout', layoutFile);
    formData.append('flatfile', flatFile);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      console.log(res)
      setParsedData(res.data.parsed);
    } catch (err) {
      console.error('Upload failed', err);
      alert('Upload failed. Check console for details.');
    }
  };

  return (
    <div>
      <h2>Upload Layout and Flat File</h2>
      <div>
        <label>Layout File (.xlsx or .csv): </label>
        <input type="file" accept=".xlsx,.xls,.csv" onChange={handleLayoutChange} />
      </div>
      <div>
        <label>Flat File (.txt): </label>
        <input type="file" accept=".txt" onChange={handleFlatFileChange} />
      </div>
      <button onClick={handleUpload}>Upload & Parse</button>

      {parsedData.length > 0 && (() => {
  // Collect all unique keys from all parsed rows
  const allKeys = Array.from(
    new Set(parsedData.flatMap(row => Object.keys(row)))
  );

  return (
    <table border="1" style={{ marginTop: '20px' }}>
      <thead>
        <tr>
          {allKeys.map((key) => (
            <th key={key}>{key}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {parsedData.map((row, idx) => (
          <tr key={idx}>
            {allKeys.map((key) => (
              <td key={key}>{row[key] || ''}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
})()}

    </div>
  );
}

export default ExcelUploader;
